import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchApartmentById } from '../mockApi';

function ApartmentDetail() {
  const { id } = useParams();
  const [apartment, setApartment] = useState(null);

  useEffect(() => {
    // Загрузка данных о квартире по идентификатору
    fetchApartmentById(parseInt(id, 10))
      .then((data) => {
        setApartment(data);
      })
      .catch((error) => {
        console.error('Error loading apartment data:', error);
      });
  }, [id]);

  if (!apartment) {
    return <div>The apartment was not found.</div>;
  }

//   return (
//     <div>
//       <h2>{apartment.name}</h2>
//       <p>Address: {apartment.address}</p>
//       <p>Price: {apartment.price} tg. per month</p>
      
//     </div>
//   );
// }



const { info, photos, price, address, advantages } = apartment;

return (
  <div>
    <h2>Apartment Details</h2>
    <div>
      <h3>Information</h3>
      <p>{apartment.name}</p>
    </div>
    <div>
      <h3>Photos</h3>
      <div>
        {/* {apartment.name} */}
      </div>
    </div>
    <div>
      <h3>Price</h3>
      <p>{apartment.price}</p>
    </div>
    <div>
      <h3>Address</h3>
      <p>{address}</p>
    </div>
    <div>
      <h3>Advantages</h3>
      <ul>
        {advantages.map((advantage, index) => (
          <li key={index}>{advantage}</li>
        ))}
      </ul>
    </div>
  </div>
);
};

export default ApartmentDetail;
