const posts = [
    {id: 1}
];

export function fetchApartments() {
  return Promise.resolve(posts);
}

export function fetchApartmentById(id) {
  const apartment = posts.find((apartment) => apartment.id === id);
  return Promise.resolve(apartment);
}
