const apartments = [
  {
    id: 1,
    name: 'Post 1',
    text: "This is "
    }
];

export function fetchApartments() {
  return Promise.resolve(apartments);
}

export function fetchApartmentById(id) {
  const apartment = apartments.find((apartment) => apartment.id === id);
  return Promise.resolve(apartment);
}
