import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchApartmentById } from '../mockApi';

function ApartmentDetail() {
  const { id } = useParams();
  const [apartment, setApartment] = useState(null);

  useEffect(() => {
    // Загрузка данных о квартире по идентификатору
    fetchApartmentById(parseInt(id, 10))
      .then((data) => {
        setApartment(data);
      })
      .catch((error) => {
        console.error('Error loading apartment data:', error);
      });
  }, [id]);

  if (!apartment) {
    return <div>The apartment was not found.</div>;
  }

return (
  <div>
    <h2 className='apartment-details'>Apartment Details</h2>
    <div>
      <h3 className='info-section'>Information</h3>
      <p>{apartment.name}</p>
    </div>
    <div>
      <h3 className='photos-section'>Photos</h3>
      <div>
        {/* {apartment.name} */}
      </div>
    </div>
    <div>
      <h3 className='price-section'>Price</h3>
      <p>{apartment.price}</p>
    </div>
    <div>
      <h3 className='address-section'>Address</h3>
      <p>{apartment.address}</p>
    </div>
    <div>
      <h3 className=''>Advantages</h3>
      <ul>
        {/* {advantages.map((advantage, index) => (
          <li key={index}>{advantage}</li>
        ))} */}
      </ul>
    </div>
  </div>
);
};

export default ApartmentDetail;
