const posts = [
    { id: 1,
      name: 'Post1',
      text: "It is the first post in this website"
    }
];

export function fetchPosts() {
  return Promise.resolve(posts);
}

export function fetchPostById(id) {
  const post = posts.find((post) => post.id === id);
  return Promise.resolve(post);
}
