import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchApartmentById } from '../mockApi';
import "./ApartmentDetail.css";

function ApartmentDetail() {
  const { id } = useParams();
  const [apartment, setApartment] = useState(null);

  useEffect(() => {
    // Загрузка данных о квартире по идентификатору
    fetchApartmentById(parseInt(id, 10))
      .then((data) => {
        setApartment(data);
      })
      .catch((error) => {
        console.error('Error loading apartment data:', error);
      });
  }, [id]);

  if (!apartment) {
    return <div>The apartment was not found.</div>;
  }

return (
  //  <div className='main'> 
    <div className='main'>
      <div>
        <div>
          <h2 className='info-section'>About apartment:</h2>
          <p>{apartment.name}</p>
          <p>{apartment.description}</p>

        </div>
        <div>
          <h3 className='price-section'>Price: <span>{apartment.price}tg per month</span></h3>
          
        </div>

        <div>
          <h3 className='address-section'>Address: <span>{apartment.address}</span></h3> 
        </div>
        
        <div>
          <h3 className='advantages-section'>Advantages:</h3> 
          <ul>
              {apartment.advantages.map((advantage, index) => (
                <li key={index}>{advantage}</li>
              ))}
            </ul>
        </div>
      </div>

      <div className="photo-gallery">
          {apartment.images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Apartment image ${index + 1}`}
              className="image"
            />
          ))}
    </div>

    <div> 
    <h2 className='author-section'>Author of </h2>
          <p>{apartment.name}</p>
          <p>{apartment.description}</p>
    </div>
 </div>
);
};

export default ApartmentDetail;
