const posts = [
  {
    id: 1,
    name: 'Post 1',
    text: "It is the first post in this website"
    }
];

export function fetchPosts() {2
  return Promise.resolve(posts);
}

export function fetchPostById(id) {
  const apartment = posts.find((apartment) => apartment.id === id);
  return Promise.resolve(apartment);
}
