import React, { useState } from 'react';
import "./Profile.css"

function Profile() {
  const [profileImage, setProfileImage] = useState(null);
  const [userName, setUserName] = useState('Username'); 
  const [userEmail, setUserEmail] = useState('example@gmail.com');
  const [userPhone, setUserPhone] = useState('+7 (777) 77 7777');
  const [tempName, setTempName] = useState(userName); 
  const [tempEmail, setTempEmail] = useState(userEmail); 
  const [tempPhone, setTempPhone] = useState(userPhone); 
  const [isEditing, setIsEditing] = useState(false);
  const [newPost, setNewPost] = useState({
    name: '',
    address: '',
    price: 0,
    rooms: '',
    image: '',
  });

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfileImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleNameChange = (e) => {
    if (isEditing) {
      setTempName(e.target.value);
    }
  };

  const handleEmailChange = (e) => {
    if (isEditing) {
      setTempEmail(e.target.value);
    }
  };

  const handlePhoneChange = (e) => {
    if (isEditing) {
      setTempPhone(e.target.value);
    }
  };

  const handleEditClick = () => {
    if (isEditing) {
     
      setUserName(tempName);
      setUserEmail(tempEmail);
      setUserPhone(tempPhone);
    } else {
      
      setTempName(userName);
      setTempEmail(userEmail);
      setTempPhone(userPhone);
    }
    setIsEditing(!isEditing);
  };

  return (
    <div>
      <div className="user-profile">
        <div className="profile-image-container">
          <img
            src={profileImage || '/images/defaultProfile.jpg'} 
            alt="Profile"
            className="profile-image"
          />
        </div>

      <div className="profileEdit">
        

        <div className="user-details">
          <div className="user-name">
            <label htmlFor="userName">Username:< br /></label>
            {isEditing ? (
              <input
                type="text"
                id="userName"
                value={tempName}
                onChange={handleNameChange}
              />
            ) : (
              <span>{userName}</span>
            )}
          </div>
          <div className="user-email">
            <label htmlFor="userEmail">Email: <br /></label>
            {isEditing ? (
              <input
                type="email"
                id="userEmail"
                value={tempEmail}
                onChange={handleEmailChange}
              />
            ) : (
              <span>{userEmail}</span>
            )}
          </div>
          <div className="user-phone">
            <label htmlFor="userPhone">Phone: <br /></label>
            {isEditing ? (
              <input
                type="tel"
                id="userPhone"
                value={tempPhone}
                onChange={handlePhoneChange}
              />
            ) : (
              <span>{userPhone}</span>
            )}
          </div>

          
        </div>
        {isEditing ? (
          <input type="file" accept="image/*" onChange={handleImageChange} />
        ) : null}
      </div>
    </div>

    <button onClick={handleEditClick} className="editButton">
        {isEditing ? 'Сохранить' : 'Редактировать'}
    </button>

    <div className="newPost">
      <h3>Add new post</h3>
      <label>Post Name</label>
      <input
          type="text"
          placeholder="post Name"
          value={newPost.name}
          onChange={(e) =>
            setNewPost({ ...newPost, name: e.target.value })
          }
        />
        <label>post Address</label>
        <input
          type="text"
          placeholder="post Address"
          value={newPost.address}
          onChange={(e) =>
            setNewPost({ ...newPost, address: e.target.value })
          }
        />
        <label>post Price</label>
        <br />
        <input
          type="number"
          placeholder="post Price"
          value={newPost.price}
          onChange={(e) =>
            setNewPost({ ...newPost, price: e.target.value })
          }
        />
        <br />
        <label>Number of Rooms</label>
        <input
          type="text"
          placeholder="Number of Rooms"
          value={newPost.rooms}
          onChange={(e) =>
            setNewPost({ ...newPost, rooms: e.target.value })
          
          }
          
        />
        <label>Add image for your post</label>
        <br />
        <input
          type="file"
          accept="image/*"
          onChange={(e) =>
            setNewPost({ ...newPost, image: e.target.files[0] })
          }
          className="imageForpost"
        />
        <br />
        {/* <button onClick={handleAddpost} className="addButton">Add</button> */}
      </div>

    </div>
  );
}

export default Profile;
